import React from "react";
import { Card, CardHeader, CardBody, CardImg, CardText, Button } from "reactstrap";

function ProdutosInfo({ product }) {
  const { selectedProduct } = useProduct();


  return (
    <>
      <div className="content">
        <Card>
          <CardHeader>{product.name}</CardHeader>
          <CardImg top width="100%" src={`data:image/png;base64,${product.image}`} alt={product.name} />
          <CardBody>
            <CardText>
              <strong>Preço:</strong> {product.price}<br />
              <strong>Quantidade:</strong> {product.quantity}<br />
              <strong>Descrição:</strong> {product.description}
            </CardText>
            <Button color="primary">Adicionar ao Carrinho</Button>
          </CardBody>
        </Card>
      </div>
    </>
  );
}

export default ProdutosInfo;
