import React, { useState, useEffect } from "react";
import { Card, CardHeader, CardGroup } from "reactstrap";
import CardProduct from "components/CardProduct/CardProduct";
import { getAllProducts } from "../axios_helper";

function Home() {
  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);
  const words = ['Camiseta', 'camiseta', 'Camiseta', 'banana', 'orange'];


  useEffect(() => {
    getAllProducts()
      .then((response) => {
        const data = response.data;
        if (Array.isArray(data)) {
          let allCategorias = Array.from(
            new Set(data.map((product) => product.categoria))
          );
          // Esta retornando um Array de tamanho 3, mas 
          allCategorias = allCategorias.filter(categoria => categoria !== null);
          console.log("allCategorias", allCategorias)

          const uniqueCategories = allCategorias.length === 2 &&
          allCategorias[0].nome === allCategorias[1].nome
            ? [allCategorias[0]]  // Se são iguais, retorna o primeiro objeto como único
            : allCategorias;
          console.log('uniqueCategories:', uniqueCategories);




          setCategories(uniqueCategories);
          setProducts(data);
        }
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  const getProductsByCategory = (categoria) => {
    const filteredProducts = products.filter((product) => product.categoria.nome === categoria);
    console.log("Filtro de produtos", filteredProducts)
    let uniqueProducts = Array.from(new Set(filteredProducts.map((product) => product.nome)));
    return uniqueProducts.map((uniqueProductName) => {
      return filteredProducts.find((product) => product.nome === uniqueProductName);
    });
  };

  return (
    <>
      <div className="content">
        {categories.map((categoria, index) => (
          <Card key={`${categoria.nome}_${index}`}>
            <CardHeader>{categoria.nome}</CardHeader>
            <CardGroup className="d-flex justify-content-center align-items-center">
              <CardProduct
                category={categoria.nome}
                products={getProductsByCategory(categoria.nome)}
              />
            </CardGroup>
          </Card>
        ))}
      </div>
    </>
  );
}

export default Home;
