import React, { useEffect, useState } from "react";
import { createProduto, createCategoria, getAllCategorias } from '../axios_helper';
import Swal from 'sweetalert2';
import { Button, Card, CardHeader, CardBody, CardFooter, FormGroup, Form, Input, Row, Col } from "reactstrap";

function Produtos() {
  const [produto, setProduto] = useState({ name: "", image: "", price: "", amount: "", description: "", categoria: "" });

  const [categoriasID, setCategoriasId] = useState([]); const [categorias, setCategorias] = useState([]); const [novaCategoria, setNovaCategoria] = useState("");

  useEffect(() => { fetchCategorias(); }, []);

  const fetchCategorias = async () => {
    try {
      const categoriasResponse = await getAllCategorias(); let categoriesVerNomes = categoriasResponse.data; let categoriasVerNomes = categoriesVerNomes.filter((element) => element.nome.trim() !== '');

      setCategorias(categoriasVerNomes);

      const categoriasID = categoriasVerNomes.map((item, index) => ({
        ...item,
        id: index + 1,
      }));

      const categoriasComIdNumerico = categoriasID.map(objeto => ({
        ...objeto,
        id: objeto.id.toString(),
      }))
      console.log("categoriasComIdNumerico", categoriasComIdNumerico);
      setCategoriasId(categoriasComIdNumerico);
    } catch (error) {
      console.error("Erro ao buscar categorias:", error.message);
    }
  };


  const handleInputChange = async (e) => {
    const { name, value, type } = e.target;

    if (type === 'file') {
      const file = e.target.files[0];
      const reader = new FileReader();

      reader.onloadend = () => {
        const arrayBuffer = reader.result;
        const byteArray = new Uint8Array(arrayBuffer);

        setProduto((prevProduto) => ({
          ...prevProduto,
          [name]: Array.from(byteArray),
        }));

      };

      reader.readAsArrayBuffer(file);
    } else {
      setProduto((prevProduto) => ({
        ...prevProduto,
        [name]: type === 'select' ? parseInt(value, 10) : value,
      }));
    }
  };


  const handleCreate = async () => {
    if (!produto.name || !produto.image || !produto.price ||
      !produto.amount || !produto.description) {
      Swal.fire({
        title: 'Error!',
        text: 'Por favor, preencha todos os campos antes de criar o produto.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
      return;
    }

    try {
      if (!produto.categoria) {
        Swal.fire({
          title: 'Error!',
          text: 'Por favor, selecione uma categoria antes de criar o produto.',
          icon: 'error',
          confirmButtonText: 'OK'
        });
        return;
      }
      const categoriaEncontrada = categoriasID.find(
        (categoria) => categoria.nome === produto.categoria
      );
      console.log("categoriaEncontrada", categoriaEncontrada)
      if (categoriaEncontrada) {
        const produtoCreated = {
          ...produto,
          categoria: categoriaEncontrada.id,
        };

        console.log("produto: create", produtoCreated)
        await createProduto(produtoCreated);
        Swal.fire({
          title: 'Success!',
          text: 'Produto criado com sucesso',
          icon: 'success',
          confirmButtonText: 'OK'
        });
      } else {
        Swal.fire({
          title: 'Error!',
          text: 'Categoria não encontrada',
          icon: 'error',
          confirmButtonText: 'OK'
        });
      }
    } catch (error) {
      Swal.fire({
        title: 'Error!',
        text: `Erro ao criar o Produto: ${error.message}`,
        icon: 'error',
        confirmButtonText: 'OK'
      });
    }
  };


  const handleCreateCategoria = async () => {
    try {
      await createCategoria({ nome: novaCategoria });
      fetchCategorias();
      setNovaCategoria("");
      Swal.fire({
        title: 'Success!',
        text: 'Categoria criada com sucesso',
        icon: 'success',
        confirmButtonText: 'OK'
      });
    } catch (error) {
      Swal.fire({
        title: 'Error!',
        text: `Erro ao criar a Categoria: ${error.message}`,
        icon: 'error',
        confirmButtonText: 'OK'
      });
    }
  };



  return (
    <>
      <div className="content">
        <Row>
          <Col md="8">
            <Card>
              <CardHeader>
                <h5 className="title">Criar Produto</h5>
              </CardHeader>
              <CardBody>
                <Form>
                  <Row>
                    <Col className="pr-md-1" md="5">
                      <FormGroup>
                        <label>Nome do Produto</label>
                        <Input
                          placeholder="Nome"
                          type="text"
                          name="name"
                          onChange={handleInputChange}
                        />
                      </FormGroup>
                    </Col>
                    <Col className="px-md-1" md="3">
                      <FormGroup>
                        <label>Preço</label>
                        <Input
                          placeholder="Preço"
                          type="text"
                          name="price"
                          onChange={handleInputChange}
                        />
                      </FormGroup>
                    </Col>
                    <Col className="pl-md-1" md="4">
                      <FormGroup>
                        <label>Quantidade</label>
                        <Input
                          placeholder="Quantidade"
                          type="text"
                          name="amount"
                          onChange={handleInputChange}
                        />
                      </FormGroup>
                    </Col>
                  </Row>
                  <Row>
                    <Col className="pr-md-1" md="4">
                      <FormGroup>
                        <label>Descrição</label>
                        <Input
                          placeholder="Descrição"
                          type="text"
                          name="description"
                          onChange={handleInputChange}
                        />
                      </FormGroup>
                    </Col>
                    <Col className="pl-md-1" md="4">
                      <FormGroup>
                        <label htmlFor="exampleInputEmail1">
                          Imagem
                        </label>
                        <Input
                          placeholder="Imagem"
                          type="file"
                          name="image"
                          onChange={handleInputChange}
                        />
                      </FormGroup>
