import React, { useState, useEffect } from "react";
import { Card, CardHeader, CardBody, Table, Button } from "reactstrap";
import { useCart } from "components/UseCart/UseCart";
import { useLocation } from "react-router-dom";
import { useProduct } from "components/ProductContext/ProductContext";

import { PaymentElement, useElements, useStripe } from "@stripe/react-stripe-js";
import { Elements } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';

function Payment() {
  const stripe = useStripe();
  const elements = useElements();
  const location = useLocation();

  const [clientSecret, setClientSecret] = useState("");
  const { cart, totalPurchase } = location.state || { cart: [], totalPurchase: 0 };
  const { removeFromCart } = useCart();
  const [cartUpdated, setCartUpdated] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [message, setMessage] = useState(null);
  const stripePromise = loadStripe('pk_test_XKUpwPvvEnNxMsSzoLm8H3i8');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!stripe || !elements) {
      // Stripe.js has not yet loaded.
      // Make sure to disable form submission until Stripe.js has loaded.
      return;
    }

    setIsProcessing(true);

    const { error } = await stripe.confirmPayment({
      elements,
      confirmParams: {
        // Make sure to change this to your payment completion page
        return_url: `${window.location.origin}/completion`,
      },
    });

    if (error.type === "card_error" || error.type === "validation_error") {
      setMessage(error.message);
    } else {
      setMessage("An unexpected error occured.");
    }

    setIsProcessing(false);
  };

  useEffect(() => {
    if (cartUpdated) {
      setCartUpdated(false);
    }
  }, [cart, cartUpdated]);

  const handleRemoveFromCart = (productId) => {
    removeFromCart(productId);
    setCartUpdated(true);
  };

  const calculateTotalPurchase = () => {
    return cart.reduce((acc, product) => acc + product.price * product.quantity, 0);
  };

  return (
    <div className="content">
      <Card>
        <CardHeader>
          <h3>Detalhes do Pagamento</h3>
        </CardHeader>
        <CardBody>
          <Table>
            <thead>
              <tr>
                <th>Imagem</th>
                <th>Produto</th>
                <th>Preço</th>
                <th>Quantidade</th>
              </tr>
            </thead>
            <tbody>
              {cart.map((product) => (
                <tr key={product.id}>
                  <td>
                    <img
                      src={`data:image/png;base64,${product.image}`}
                      alt={product.name}
                      style={{ maxWidth: "50px", maxHeight: "50px" }}
                    />
                  </td>
                  <td>{product.name}</td>
                  <td>R${product.price}</td>
                  <td>{product.quantity}</td>
                  <td>
                    <Button
                      color="danger"
                      size="sm"
                      onClick={() => handleRemoveFromCart(product.id)}
                    >
                      Remover
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
          <div>Total da Compra: R${calculateTotalPurchase()}</div>

        <Elements stripe={stripePromise} options={{ clientSecret }}>
            <form id="payment-form" onSubmit={handleSubmit}>
              <PaymentElement id="payment-element" />
              <button disabled={isProcessing || !stripe || !elements} id="submit">
                <span id="button-text">
                  {isProcessing ? "Processing ... " : "Pay now"}
                </span>
              </button>
              {/* Show any error or success messages */}
              {message && <div id="payment-message">{message}</div>}
            </form>
          </Elements>
        </CardBody>

      </Card>
    </div>
  );
}

export default Payment;
