import React, { useState, useEffect } from "react";
import { Card, CardHeader, CardGroup } from "reactstrap";
import CardProduct from "components/CardProduct/CardProduct";
import { getAllProducts } from "../axios_helper";

function Home() {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    getAllProducts()
      .then((response) => {
        const data = response.data;
        if (Array.isArray(data)) {
          const uniqueCategories = Array.from(
            new Set(data.map((product) => product.categoria))
          );
          setCategories(uniqueCategories);
        }
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  const getProductsByCategory = (categoria, allProducts) => {
    return allProducts.filter((product) => product.categoria === categoria);
  };

  return (
    <>
      <div className="content">
        {categories.map((categoria) => (
          <Card key={categoria}>
            <CardHeader>{categoria}</CardHeader>
            <CardGroup className="d-flex justify-content-center align-items-center">
              <CardProduct
                category={categoria.nome}
                products={getProductsByCategory(categoria.nome, categories)}
              />

            </CardGroup>
          </Card>
        ))}
      </div>
    </>
  );
}

export default Home;
