import React, { useState, useEffect } from "react";
import {
  Card,
  CardBody,
  CardFooter,
  CardTitle,
  Row,
  Col,
  Button,
} from "reactstrap";
import {
  Carousel,
  CarouselItem,
  CarouselControl,
  CarouselIndicators,
  CarouselCaption,
} from "reactstrap";

const images = [
  "../../assets/img/camiseta1.png",
  "https://picsum.photos/id/456/1200/400",
  "https://picsum.photos/id/678/1200/400",
];

function CardProduct() {
  const [activeIndex, setActiveIndex] = useState(0);
  const [animating, setAnimating] = useState(false);

  const next = () => {
    if (animating) return;
    const nextIndex = activeIndex === images.length - 1 ? 0 : activeIndex + 1;
    setActiveIndex(nextIndex);
  };

  const previous = () => {
    if (animating) return;
    const nextIndex = activeIndex === 0 ? images.length - 1 : activeIndex - 1;
    setActiveIndex(nextIndex);
  };

  const goToIndex = (newIndex) => {
    if (animating) return;
    setActiveIndex(newIndex);
  };

  useEffect(() => {
    const interval = setInterval(() => {
      next();
    }, 3000);

    return () => clearInterval(interval);
  }, [activeIndex]);

  return (
    <Row>
      <Col md="12">
        <Card>
          <Col>
            <Button>
              <Carousel
                activeIndex={activeIndex}
                next={next}
                previous={previous}
                interval={false}
              >
                <CarouselIndicators
                  items={images}
                  activeIndex={activeIndex}
                  onClickHandler={goToIndex}
                />
                {images.map((src, index) => (
                  <CarouselItem key={index}>
                    <img src={src} alt={`Slide ${index + 1}`} />
                    <CarouselCaption
                      captionText={`Slide ${index + 1}`}
                      captionHeader={`Slide ${index + 1}`}
                    />
                  </CarouselItem>
                ))}
                <CarouselControl
                  direction="prev"
                  directionText="Previous"
                  onClickHandler={previous}
                />
                <CarouselControl
                  direction="next"
                  directionText="Next"
                  onClickHandler={next}
                />
              </Carousel>

              <CardTitle>{/* Adicione o título do seu produto aqui */}</CardTitle>
              <CardBody>
                <img
                  alt="..."
                  className="image"
                  src={require("../../assets/img/camiseta1.png")}
                />
              </CardBody>
              <CardFooter>
                <p className="text-danger">{/* Adicione o preço do seu produto aqui */}</p>
              </CardFooter>
            </Button>
          </Col>
        </Card>
      </Col>
    </Row>
  );
}

export default CardProduct;
