                          type="text"
                          name="facebook"
                          value={user.facebook}
                          onChange={handleChange}
                        />
                      </FormGroup>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="12">
                      <FormGroup>
                        <label>Gmail</label>
                        <Input
                          placeholder="Gmail Address"
                          type="text"
                          name="gmail"
                          value={user.gmail}
                          onChange={handleChange}
                        />
                      </FormGroup>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="12">
                      <FormGroup>
                        <label>Twitter</label>
                        <Input
                          placeholder="Twitter Profile URL"
                          type="text"
                          name="twitter"
                          value={user.twitter}
                          onChange={handleChange}
                        />
                      </FormGroup>
                    </Col>
                  </Row>
                </Form>
              </CardBody>
              <CardFooter>
                <Button
                  className="btn-fill"
                  color="primary"
                  type="button"
                  onClick={handleSave}
                >
                  Save
                </Button>
              </CardFooter>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}


export default Produtos;
